@import url('https://fonts.googleapis.com/css2?family=Inter:wght@200;300;400;500;600;700;800;900&family=Figtree:wght@300;400;500;600;700;800;900&family=Satoshi:wght@300;400;500;600;700;800;900&display=swap');
@import url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css');

@tailwind base;
@tailwind components;
@tailwind utilities;

/* Mobile-first responsive design improvements */
@layer base {
  /* Ensure inputs are properly sized on mobile */
  input, textarea, select {
    font-size: 16px !important; /* Prevents zoom on iOS */
  }

  /* Better touch targets on mobile */
  button, a {
    min-height: 44px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  /* Improve table readability on mobile */
  @media (max-width: 640px) {
    table {
      font-size: 14px;
    }
  }

  /* Modern typography */
  body {
    background: hsl(var(--background));
    min-height: 100vh;
    font-family: 'Inter', 'Figtree', system-ui, -apple-system, sans-serif;
    font-optical-sizing: auto;
    font-weight: 400;
    font-style: normal;
    letter-spacing: -0.01em;
    line-height: 1.6;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  /* Modern glass morphism */
  .glass {
    background: hsl(var(--card) / 0.8);
    backdrop-filter: blur(20px);
    border: 1px solid hsl(var(--border));
    box-shadow: var(--shadow-lg);
    border-radius: calc(var(--radius) * 1.5);
  }

  .dark .glass {
    background: hsl(var(--card) / 0.9);
    border: 1px solid hsl(var(--border));
    box-shadow: var(--shadow-xl);
  }

  /* Modern gradient backgrounds */
  .gradient-primary {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(263 70% 70%) 100%);
  }

  .gradient-subtle {
    background: linear-gradient(135deg, hsl(var(--primary) / 0.05) 0%, hsl(var(--accent)) 100%);
  }

  .gradient-animated {
    background: linear-gradient(-45deg, hsl(var(--primary)), hsl(263 70% 60%), hsl(262 83% 70%), hsl(220 14% 96%));
    background-size: 400% 400%;
    animation: gradientShift 15s ease infinite;
  }

  @keyframes gradientShift {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }

  /* Typography utilities */
  .font-display {
    font-family: 'Figtree', 'Inter', system-ui, sans-serif;
    font-weight: 600;
    letter-spacing: -0.025em;
  }

  .font-body {
    font-family: 'Inter', system-ui, sans-serif;
    letter-spacing: -0.01em;
  }

  /* Premium shadows */
  .shadow-premium {
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  }

  /* Enhanced hover animations */
  .hover-lift {
    @apply transition-all duration-500 ease-out;
    transform-style: preserve-3d;
  }

  .hover-lift:hover {
    @apply transform -translate-y-2;
    box-shadow: 0 20px 40px rgba(255, 20, 147, 0.2), 0 8px 16px rgba(255, 20, 147, 0.1);
  }

  /* Enhanced button animations */
  .btn-animate {
    @apply transition-all duration-300 ease-in-out;
    position: relative;
    overflow: hidden;
  }

  .btn-animate::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: left 0.5s ease;
  }

  .btn-animate:hover::before {
    left: 100%;
  }

  .btn-animate:hover {
    @apply transform scale-105;
    box-shadow: 0 8px 25px rgba(255, 20, 147, 0.3);
  }

  .btn-animate:active {
    @apply transform scale-95;
  }

  /* Floating animation */
  .float {
    animation: float 6s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  /* Fade in animations */
  .fade-in {
    animation: fadeIn 0.8s ease-out forwards;
  }

  .fade-in-up {
    animation: fadeInUp 0.8s ease-out forwards;
  }

  .fade-in-delay-1 { animation-delay: 0.1s; }
  .fade-in-delay-2 { animation-delay: 0.2s; }
  .fade-in-delay-3 { animation-delay: 0.3s; }

  @keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
  }

  @keyframes fadeInUp {
    from { opacity: 0; transform: translateY(30px); }
    to { opacity: 1; transform: translateY(0); }
  }

  /* Pulse animation */
  .pulse-soft {
    animation: pulseSoft 2s ease-in-out infinite;
  }

  @keyframes pulseSoft {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
  }
}

:root {
