import { useState, useRef } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";

interface MediaItem {
  id: string;
  type: 'image' | 'video';
  url: string;
  title: string;
  description: string;
}

interface ContentSectionProps {
  onContentChange?: (content: ContentSectionData) => void;
}

export interface ContentSectionData {
  title: string;
  description: string;
  mediaItems: MediaItem[];
}

export default function ContentSection({ onContentChange }: ContentSectionProps) {
  const { toast } = useToast();
  const [content, setContent] = useState<ContentSectionData>({
    title: "",
    description: "",
    mediaItems: []
  });

  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleContentUpdate = (field: keyof ContentSectionData, value: any) => {
    const updatedContent = { ...content, [field]: value };
    setContent(updatedContent);
    onContentChange?.(updatedContent);
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (!files) return;

    Array.from(files).forEach((file) => {
      const reader = new FileReader();
      reader.onload = (e) => {
        const result = e.target?.result as string;
        const mediaType = file.type.startsWith('video/') ? 'video' : 'image';

        const newMediaItem: MediaItem = {
          id: `media-${Date.now()}-${Math.random()}`,
          type: mediaType,
          url: result,
          title: file.name,
          description: `${mediaType} uploaded on ${new Date().toLocaleDateString()}`
        };

        const updatedMediaItems = [...content.mediaItems, newMediaItem];
        handleContentUpdate('mediaItems', updatedMediaItems);

        toast({
          title: "Media uploaded successfully",
          description: `${file.name} has been added to your content section.`,
        });
      };
      reader.readAsDataURL(file);
    });
  };

  const removeMediaItem = (id: string) => {
    const updatedMediaItems = content.mediaItems.filter(item => item.id !== id);
    handleContentUpdate('mediaItems', updatedMediaItems);

    toast({
      title: "Media removed",
      description: "Media item has been removed from your content section.",
    });
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <i className="fas fa-photo-video text-primary"></i>
          Content & Media Section
        </CardTitle>
        <p className="text-sm text-gray-600">
          Add images, videos, and rich content to enhance your invoice presentation
        </p>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Title & Description */}
        <div className="space-y-4">
          <div>
            <Label htmlFor="content-title">Content Title</Label>
            <Input
              id="content-title"
              value={content.title}
              onChange={(e) => handleContentUpdate('title', e.target.value)}
              placeholder="e.g., Project Gallery, Work Samples, etc."
              className="mt-1"
            />
          </div>

          <div>
            <Label htmlFor="content-description">Description</Label>
            <Textarea
              id="content-description"
              value={content.description}
              onChange={(e) => handleContentUpdate('description', e.target.value)}
              placeholder="Describe what this content represents..."
              rows={3}
              className="mt-1"
            />
          </div>
        </div>

        {/* Media Upload */}
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <Label>Media Files</Label>
            <Button
              type="button"
              variant="outline"
              onClick={() => fileInputRef.current?.click()}
              className="flex items-center gap-2"
            >
              <i className="fas fa-cloud-upload-alt"></i>
              Upload Media
            </Button>
          </div>

          <input
            ref={fileInputRef}
            type="file"
            multiple
            accept="image/*,video/*"
            onChange={handleFileUpload}
            className="hidden"
          />

          {/* Media Grid */}
          {content.mediaItems.length > 0 && (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {content.mediaItems.map((item) => (
                <div key={item.id} className="relative group">
                  <Card className="overflow-hidden">
                    <CardContent className="p-0">
                      {item.type === 'image' ? (
                        <img
                          src={item.url}
                          alt={item.title}
                          className="w-full h-32 object-cover"
                        />
                      ) : (
                        <video
                          src={item.url}
                          className="w-full h-32 object-cover"
                          controls
                        />
                      )}
                      <div className="p-3">
                        <h4 className="text-sm font-medium truncate">{item.title}</h4>
                        <p className="text-xs text-gray-500 truncate">{item.description}</p>
                      </div>
                    </CardContent>
                  </Card>

                  <Button
                    type="button"
                    variant="destructive"
                    size="sm"
                    className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-opacity w-6 h-6 p-0"
                    onClick={() => removeMediaItem(item.id)}
                  >
                    <i className="fas fa-times text-xs"></i>
                  </Button>
                </div>
              ))}
            </div>
          )}

          {content.mediaItems.length === 0 && (
            <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center">
              <i className="fas fa-photo-video text-3xl text-gray-400 mb-3"></i>
              <p className="text-gray-500 mb-2">No media files uploaded yet</p>
              <p className="text-sm text-gray-400">
                Support for images and videos (JPEG, PNG, MP4, etc.)
              </p>
            </div>
          )}
        </div>

        {/* Content Preview */}
        {(content.title || content.description || content.mediaItems.length > 0) && (
          <div className="border-t pt-4">
            <h4 className="text-sm font-medium text-gray-700 mb-3">Preview</h4>
            <div className="bg-gray-50 rounded-lg p-4">
              {content.title && (
                <h3 className="text-lg font-semibold text-gray-900 mb-2">{content.title}</h3>
              )}
              {content.description && (
                <p className="text-gray-600 mb-3">{content.description}</p>
              )}
              <p className="text-xs text-gray-500">
                {content.mediaItems.length} media item{content.mediaItems.length !== 1 ? 's' : ''} attached
              </p>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/email-modal.tsx.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import type { EmailFormData } from "@/types/invoice";

interface EmailModalProps {
  isOpen: boolean;
  onClose: () => void;
  invoiceId: string;
  invoiceNumber: string;
  companyName: string;
  clientEmail?: string;
}

export default function EmailModal({
  isOpen,
  onClose,
  invoiceId,
  invoiceNumber,
  companyName,
  clientEmail,
}: EmailModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [formData, setFormData] = useState<EmailFormData>({
    to: clientEmail || "",
    subject: `Invoice ${invoiceNumber} from ${companyName}`,
    message: `Dear Client,\n\nPlease find attached your invoice.\n\nThank you for your business!\n\nBest regards,\n${companyName}`,
    attachPDF: true,
  });

  const sendEmailMutation = useMutation({
    mutationFn: async (data: EmailFormData) => {
      const response = await apiRequest("POST", `/api/invoices/${invoiceId}/send-email`, data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Email sent successfully!",
        description: "The invoice has been sent to the recipient.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/invoices"] });
      onClose();
      setFormData({
        to: clientEmail || "",
        subject: `Invoice ${invoiceNumber} from ${companyName}`,
        message: `Dear Client,\n\nPlease find attached your invoice.\n\nThank you for your business!\n\nBest regards,\n${companyName}`,
        attachPDF: true,
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to send email",
        description: error.message || "An error occurred while sending the email.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.to.trim()) {
      toast({
        title: "Email required",
        description: "Please enter a recipient email address.",
        variant: "destructive",
      });
      return;
    }
    sendEmailMutation.mutate(formData);
  };

  const handleChange = (field: keyof EmailFormData, value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-md w-[95vw] max-h-[90vh] overflow-y-auto mx-4 bg-white dark:bg-gray-900 rounded-xl shadow-2xl border-0 sm:border fixed left-1/2 top-1/2 translate-x-[-50%] translate-y-[-50%] z-[9999]">
        <DialogHeader>
          <DialogTitle className="flex items-center text-base sm:text-lg">
            <i className="fas fa-envelope mr-2 text-primary"></i>
            Send Invoice
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="to" className="text-sm font-medium text-gray-700">
              To Email Address *
            </Label>
            <Input
              id="to"
              type="email"
              value={formData.to}
              onChange={(e) => handleChange("to", e.target.value)}
              placeholder="client@example.com"
              required
              className="mt-1"
            />
          </div>

          <div>
            <Label htmlFor="subject" className="text-sm font-medium text-gray-700">
              Subject
            </Label>
            <Input
              id="subject"
              type="text"
              value={formData.subject}
              onChange={(e) => handleChange("subject", e.target.value)}
              className="mt-1"
            />
          </div>

          <div>
            <Label htmlFor="message" className="text-sm font-medium text-gray-700">
              Message
            </Label>
            <Textarea
              id="message"
              value={formData.message}
              onChange={(e) => handleChange("message", e.target.value)}
              rows={4}
              className="mt-1"
              placeholder="Enter your message here..."
            />
          </div>

          <div className="flex items-center space-x-2">
            <Checkbox
              id="attachPDF"
              checked={formData.attachPDF}
              onCheckedChange={(checked) => handleChange("attachPDF", !!checked)}
            />
            <Label htmlFor="attachPDF" className="text-sm text-gray-700">
              Attach PDF invoice
            </Label>
          </div>

          <div className="flex flex-col sm:flex-row space-y-3 sm:space-y-0 sm:space-x-3 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={onClose}
              className="w-full sm:flex-1 py-3"
              disabled={sendEmailMutation.isPending}
            >
              Cancel
            </Button>
            <Button
              type="submit"
              className="w-full sm:flex-1 bg-primary hover:bg-primary-dark py-3"
              disabled={sendEmailMutation.isPending}
            >
              {sendEmailMutation.isPending ? (
                <>
                  <i className="fas fa-spinner fa-spin mr-2"></i>
                  Sending...
                </>
              ) : (
                <>
                  <i className="fas fa-paper-plane mr-2"></i>
                  Send Email
                </>
              )}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/google-ads.tsx.content:
import { useEffect, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";

interface GoogleAdsProps {
  adSlot?: string;
  adFormat?: 'horizontal' | 'vertical' | 'square';
  className?: string;
}

export default function GoogleAds({ 
  adSlot = "demo-ad-slot", 
  adFormat = "horizontal",
  className = "" 
}: GoogleAdsProps) {
  const [adLoaded, setAdLoaded] = useState(false);
  const [showDemo, setShowDemo] = useState(true);

  useEffect(() => {
    // Simulate ad loading
    const timer = setTimeout(() => {
      setAdLoaded(true);
    }, 2000);

    return () => clearTimeout(timer);
  }, []);

  const getDemoAdContent = () => {
    const ads = [
      {
        title: "Professional Templates",
        description: "Create stunning invoices with our premium templates",
        cta: "Upgrade Now",
        color: "bg-blue-500"
      },
      {
        title: "Invoice Analytics",
        description: "Track payments and client insights with our dashboard",
        cta: "Learn More", 
        color: "bg-green-500"
      },
      {
        title: "Auto Payment Reminders",
        description: "Never miss a payment with automated follow-ups",
        cta: "Try Free",
        color: "bg-purple-500"
      },
      {
        title: "Multi-Currency Support",
        description: "Invoice globally with 50+ currencies supported",
        cta: "Explore",
        color: "bg-orange-500"
      }
    ];

    return ads[Math.floor(Math.random() * ads.length)];
  };

  const demoAd = getDemoAdContent();

  const getAdDimensions = () => {
    switch (adFormat) {
      case 'vertical':
        return 'w-full h-96';
      case 'square':
        return 'w-full aspect-square';
      case 'horizontal':
      default:
        return 'w-full h-24';
    }
  };

  if (showDemo) {
    return (
      <Card className={`${className} overflow-hidden`}>
        <CardContent className="p-0">
          <div className={`${getAdDimensions()} ${demoAd.color} text-white relative`}>
            <div className="absolute inset-0 bg-gradient-to-r from-black/20 to-transparent"></div>
            <div className="relative h-full flex flex-col justify-center p-4">
              <div className="flex items-center justify-between mb-2">
                <span className="text-xs bg-white/20 px-2 py-1 rounded">Advertisement</span>
                <Button
                  variant="ghost"
                  size="sm"
                  className="text-white/70 hover:text-white p-1 h-auto"
                  onClick={() => setShowDemo(false)}
                >
                  <i className="fas fa-times text-xs"></i>
                </Button>
              </div>

              <h3 className="text-lg font-bold mb-1">{demoAd.title}</h3>
              <p className="text-sm text-white/90 mb-3">{demoAd.description}</p>

              <Button 
                size="sm" 
                variant="secondary" 
                className="self-start bg-white text-gray-900 hover:bg-gray-100"
              >
                {demoAd.cta}
              </Button>
            </div>

            <div className="absolute bottom-1 right-1">
              <span className="text-xs text-white/60">Sponsored</span>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className={`${className} overflow-hidden`}>
      <CardContent className="p-4">
        <div className={`${getAdDimensions()} border-2 border-dashed border-gray-300 rounded flex items-center justify-center bg-gray-50`}>
          {!adLoaded ? (
            <div className="text-center">
              <i className="fas fa-spinner fa-spin text-2xl text-gray-400 mb-2"></i>
              <p className="text-sm text-gray-500">Loading advertisement...</p>
            </div>
          ) : (
            <div className="text-center">
              <i className="fas fa-ad text-2xl text-gray-400 mb-2"></i>
              <p className="text-sm text-gray-500">Google Ads Placeholder</p>
              <p className="text-xs text-gray-400 mt-1">Ad Slot: {adSlot}</p>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

// Google Ads Integration Component
export function GoogleAdsScript() {
  useEffect(() => {
    // Add Google AdSense script to document head only once
    const scriptId = 'google-adsense-script';
    if (!document.getElementById(scriptId)) {
      const script = document.createElement('script');
      script.id = scriptId;
      script.async = true;
      script.src = 'https://pagead2.googlesyndication.com/pagead/js/adsbygoogle.js?client=ca-pub-demo';
      script.crossOrigin = 'anonymous';
      document.head.appendChild(script);

      // Initialize ads after a delay
      setTimeout(() => {
        try {
          ((window as any).adsbygoogle = (window as any).adsbygoogle || []).push({});
        } catch (e) {
          console.log('AdSense initialization delayed');
        }
      }, 1000);
    }
  }, []);

  return null;
}

// Responsive Google Ad Component
export function ResponsiveGoogleAd({ 
  adSlot, 
  className = "",
  adFormat = "auto" 
}: {
  adSlot: string;
  className?: string;
  adFormat?: string;
}) {
  return (
    <div className={`google-ad-container ${className}`}>
      <ins
        className="adsbygoogle"
        style={{ display: 'block' }}
        data-ad-client="ca-pub-XXXXXXXXXX"
        data-ad-slot={adSlot}
        data-ad-format={adFormat}
        data-full-width-responsive="true"
      />
    </div>
  );
}

client/src/components/invoice-card.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import type { InvoiceWithLineItems } from "@shared/schema";

interface InvoiceCardProps {
  invoice: InvoiceWithLineItems;
  onView: (invoice: InvoiceWithLineItems) => void;
  onEdit: (invoice: InvoiceWithLineItems) => void;
  onDelete: (invoice: InvoiceWithLineItems) => void;
}

export default function InvoiceCard({ invoice, onView, onEdit, onDelete }: InvoiceCardProps) {
  const getStatusColor = (status: string) => {
    switch (status) {
      case "paid":
        return "bg-green-100 text-green-800";
      case "sent":
        return "bg-yellow-100 text-yellow-800";
      case "overdue":
        return "bg-red-100 text-red-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <Card className="hover:shadow-md transition-shadow">
      <CardContent className="p-6">
        <div className="flex justify-between items-start mb-4">
          <div>
            <h3 className="font-semibold text-lg text-gray-900">
              #{invoice.invoiceNumber}
            </h3>
            <p className="text-sm text-gray-600">{invoice.clientName}</p>
          </div>
          <Badge className={`text-xs font-medium px-2.5 py-0.5 rounded-full ${getStatusColor(invoice.status)}`}>
            {invoice.status.charAt(0).toUpperCase() + invoice.status.slice(1)}
          </Badge>
        </div>

        <div className="space-y-2 mb-4">
          <div className="flex justify-between text-sm">
            <span className="text-gray-600">Date:</span>
            <span className="font-medium">{formatDate(invoice.invoiceDate)}</span>
          </div>
          <div className="flex justify-between text-sm">
            <span className="text-gray-600">Amount:</span>
            <span className="font-bold text-primary">${invoice.total}</span>
          </div>
          {invoice.dueDate && (
            <div className="flex justify-between text-sm">
              <span className="text-gray-600">Due:</span>
              <span className="font-medium">{formatDate(invoice.dueDate)}</span>
            </div>
          )}
        </div>

        <div className="flex space-x-2">
          <Button
            variant="outline"
            size="sm"
            onClick={() => onView(invoice)}
            className="flex-1"
          >
            <i className="fas fa-eye mr-1"></i>View
          </Button>
          <Button
            size="sm"
            onClick={() => onEdit(invoice)}
            className="flex-1 bg-primary hover:bg-primary-dark"
          >
            <i className="fas fa-edit mr-1"></i>Edit
          </Button>
          <Button
            variant="outline"
            size="sm"
            onClick={() => onDelete(invoice)}
            className="text-red-600 hover:text-red-700 hover:bg-red-50"
          >
            <i className="fas fa-trash"></i>
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/invoice-form.tsx.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Palette } from "lucide-react";
import LogoUpload from "./logo-upload";
import ContentSection, { type ContentSectionData } from "./content-section";
import { INVOICE_TEMPLATES, CURRENCIES, FONT_FAMILIES, DOCUMENT_TYPES, getTemplateById } from "@/lib/invoice-templates";
import type { InvoiceFormData, LineItemFormData } from "@/types/invoice";

interface InvoiceFormProps {
  formData: InvoiceFormData;
  lineItems: LineItemFormData[];
  onFormChange: (field: keyof InvoiceFormData, value: any) => void;
  onLineItemsChange: (items: LineItemFormData[]) => void;
}

interface ExtendedInvoiceFormData extends InvoiceFormData {
  contentSection?: ContentSectionData;
}

export default function InvoiceForm({
  formData,
  lineItems,
  onFormChange,
  onLineItemsChange,
}: InvoiceFormProps) {
  const [contentSection, setContentSection] = useState<ContentSectionData>({
    title: "",
    description: "",
    mediaItems: []
  });

  const handleContentChange = (content: ContentSectionData) => {
    setContentSection(content);
    // Store in localStorage for persistence
    localStorage.setItem('invoice-content-section', JSON.stringify(content));
  };

  // Load content section from localStorage on mount
  useEffect(() => {
    const savedContent = localStorage.getItem('invoice-content-section');
    if (savedContent) {
      try {
        setContentSection(JSON.parse(savedContent));
      } catch (e) {
        console.error('Failed to parse saved content section');
      }
    }
  }, []);

  const handleLineItemChange = (index: number, field: keyof LineItemFormData, value: string | number) => {
    const updatedItems = [...lineItems];
    updatedItems[index] = { ...updatedItems[index], [field]: value };

    // Calculate amount when rate or quantity changes
    if (field === 'rate' || field === 'quantity') {
      const rate = field === 'rate' ? parseFloat(value.toString()) : parseFloat(updatedItems[index].rate);
      const quantity = field === 'quantity' ? Number(value) : updatedItems[index].quantity;
      updatedItems[index].amount = ((rate || 0) * (quantity || 0)).toFixed(2);
    }

    onLineItemsChange(updatedItems);
  };

  const addLineItem = () => {
    onLineItemsChange([
      ...lineItems,
      { description: "", quantity: 1, rate: "0.00", amount: "0.00" }
    ]);
  };

  const removeLineItem = (index: number) => {
    const updatedItems = lineItems.filter((_, i) => i !== index);
    onLineItemsChange(updatedItems);
  };

  // Initialize with one line item if empty
  useEffect(() => {
    if (lineItems.length === 0) {
      addLineItem();
    }
  }, []);

  return (
    <div className="space-y-6">
      {/* Company Information */}
      <Card>
        <CardContent className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Company Information</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="md:col-span-2">
              <LogoUpload
                onLogoChange={(logo) => onFormChange("companyLogo", logo)}
                currentLogo={formData.companyLogo}
              />
            </div>
            <div>
              <Label htmlFor="companyName">Company Name *</Label>
              <Input
                id="companyName"
                value={formData.companyName}
                onChange={(e) => onFormChange("companyName", e.target.value)}
                placeholder="Acme Corporation"
                className="mt-1"
              />
            </div>
            <div>
              <Label htmlFor="companyEmail">Email Address *</Label>
              <Input
                id="companyEmail"
                type="email"
                value={formData.companyEmail}
                onChange={(e) => onFormChange("companyEmail", e.target.value)}
                placeholder="hello@acmecorp.com"
                className="mt-1"
              />
            </div>
            <div>
              <Label htmlFor="companyPhone">Phone Number</Label>
              <Input
                id="companyPhone"
                type="tel"
                value={formData.companyPhone}
                onChange={(e) => onFormChange("companyPhone", e.target.value)}
                placeholder="+1 (555) 123-4567"
                className="mt-1"
              />
            </div>
            <div>
              <Label htmlFor="companyWebsite">Website</Label>
              <Input
                id="companyWebsite"
                type="url"
                value={formData.companyWebsite}
                onChange={(e) => onFormChange("companyWebsite", e.target.value)}
                placeholder="www.acmecorp.com"
                className="mt-1"
              />
            </div>
            <div className="md:col-span-2">
              <Label htmlFor="companyAddress">Address</Label>
              <Textarea
                id="companyAddress"
                value={formData.companyAddress}
                onChange={(e) => onFormChange("companyAddress", e.target.value)}
                rows={3}
                placeholder="123 Business St, Suite 100&#10;New York, NY 10001"
                className="mt-1"
              />
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Client Information */}
      <Card>
        <CardContent className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Bill To</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label htmlFor="clientName">Client Name *</Label>
              <Input
                id="clientName"
                value={formData.clientName}
                onChange={(e) => onFormChange("clientName", e.target.value)}
                placeholder="John Smith"
                className="mt-1"
              />
            </div>
            <div>
              <Label htmlFor="clientEmail">Client Email *</Label>
              <Input
                id="clientEmail"
                type="email"
                value={formData.clientEmail}
                onChange={(e) => onFormChange("clientEmail", e.target.value)}
                placeholder="john@example.com"
                className="mt-1"
              />
            </div>
            <div>
              <Label htmlFor="clientCompany">Company</Label>
              <Input
                id="clientCompany"
                value={formData.clientCompany}
                onChange={(e) => onFormChange("clientCompany", e.target.value)}
                placeholder="Client Company Inc"
                className="mt-1"
              />
            </div>
            <div>
              <Label htmlFor="clientPhone">Phone</Label>
              <Input
                id="clientPhone"
                type="tel"
                value={formData.clientPhone}
                onChange={(e) => onFormChange("clientPhone", e.target.value)}
                placeholder="+1 (555) 987-6543"
                className="mt-1"
              />
            </div>
            <div className="md:col-span-2">
              <Label htmlFor="clientAddress">Billing Address</Label>
              <Textarea
                id="clientAddress"
                value={formData.clientAddress}
                onChange={(e) => onFormChange("clientAddress", e.target.value)}
                rows={3}
                placeholder="456 Client Ave&#10;Los Angeles, CA 90210"
                className="mt-1"
              />
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Invoice Details */}
      <Card>
        <CardContent className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Invoice Details</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <Label htmlFor="invoiceNumber">Invoice Number *</Label>
              <Input
                id="invoiceNumber"
                value={formData.invoiceNumber}
                onChange={(e) => onFormChange("invoiceNumber", e.target.value)}
                placeholder="INV-001"
                className="mt-1"
              />
            </div>
            <div>
              <Label htmlFor="invoiceDate">Invoice Date *</Label>
              <Input
                id="invoiceDate"
                type="date"
                value={formData.invoiceDate}
                onChange={(e) => onFormChange("invoiceDate", e.target.value)}
                className="mt-1"
              />
            </div>
            <div>
              <Label htmlFor="dueDate">Due Date</Label>
              <Input
                id="dueDate"
                type="date"
                value={formData.dueDate}
                onChange={(e) => onFormChange("dueDate", e.target.value)}
                className="mt-1"
              />
            </div>
          </div>
          <div className="mt-4">
            <Label htmlFor="notes">Notes</Label>
            <Textarea
              id="notes"
              value={formData.notes}
              onChange={(e) => onFormChange("notes", e.target.value)}
              rows={3}
              placeholder="Thank you for your business!"
              className="mt-1"
            />
          </div>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mt-4">
            <div>
              <Label htmlFor="documentType">Document Type</Label>
              <Select value={formData.documentType} onValueChange={(value) => onFormChange("documentType", value)}>
                <SelectTrigger className="mt-1">
                  <SelectValue placeholder="Select type" />
                </SelectTrigger>
                <SelectContent>
                  {DOCUMENT_TYPES.map((type) => (
                    <SelectItem key={type.value} value={type.value}>
                      {type.icon} {type.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="currency">Currency</Label>
              <Select value={formData.currency} onValueChange={(value) => onFormChange("currency", value)}>
                <SelectTrigger className="mt-1">
                  <SelectValue placeholder="Select currency" />
                </SelectTrigger>
                <SelectContent>
                  {CURRENCIES.map((currency) => (
                    <SelectItem key={currency.code} value={currency.code}>
                      {currency.symbol} {currency.code} - {currency.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="taxPercentage">Tax Percentage (%)</Label>
              <Input
                id="taxPercentage"
                type="number"
                min="0"
                max="100"
                step="0.01"
                value={formData.taxPercentage}
                onChange={(e) => onFormChange("taxPercentage", e.target.value)}
                placeholder="8.25"
                className="mt-1"
              />
            </div>
            <div>
              <Label htmlFor="shippingCost">Shipping Cost</Label>
              <Input
                id="shippingCost"
                type="number"
                min="0"
                step="0.01"
                value={formData.shippingCost}
                onChange={(e) => onFormChange("shippingCost", e.target.value)}
                placeholder="15.00"
                className="mt-1"
              />
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Template & Design */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Palette className="h-5 w-5" />
            Template & Design
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div>
            <Label htmlFor="template">Invoice Template</Label>
            <Select value={formData.template} onValueChange={(value) => {
              const template = getTemplateById(value);
              onFormChange("template", value);
              onFormChange("primaryColor", template.primaryColor);
              onFormChange("secondaryColor", template.secondaryColor);
              onFormChange("fontFamily", template.fontFamily);
            }}>
              <SelectTrigger className="mt-1">
                <SelectValue placeholder="Select template" />
              </SelectTrigger>
              <SelectContent>
                {INVOICE_TEMPLATES.map((template) => (
                  <SelectItem key={template.id} value={template.id}>
                    {template.preview} {template.name} - {template.description}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <Label htmlFor="primaryColor">Primary Color</Label>
              <div className="flex gap-2 mt-1">
                <Input
                  id="primaryColor"
                  type="color"
                  value={formData.primaryColor}
                  onChange={(e) => onFormChange("primaryColor", e.target.value)}
                  className="w-12 h-10 p-1 border-2"
                />
                <Input
                  type="text"
                  value={formData.primaryColor}
                  onChange={(e) => onFormChange("primaryColor", e.target.value)}
                  placeholder="#2563eb"
                  className="flex-1"
                />
              </div>
            </div>
            <div>
              <Label htmlFor="secondaryColor">Secondary Color</Label>
              <div className="flex gap-2 mt-1">
                <Input
                  id="secondaryColor"
                  type="color"
                  value={formData.secondaryColor}
                  onChange={(e) => onFormChange("secondaryColor", e.target.value)}
                  className="w-12 h-10 p-1 border-2"
                />
                <Input
                  type="text"
                  value={formData.secondaryColor}
                  onChange={(e) => onFormChange("secondaryColor", e.target.value)}
                  placeholder="#64748b"
                  className="flex-1"
                />
              </div>
            </div>
            <div>
              <Label htmlFor="fontFamily">Font Family</Label>
              <Select value={formData.fontFamily} onValueChange={(value) => onFormChange("fontFamily", value)}>
                <SelectTrigger className="mt-1">
                  <SelectValue placeholder="Select font" />
                </SelectTrigger>
                <SelectContent>
                  {FONT_FAMILIES.map((font) => (
                    <SelectItem key={font.value} value={font.value}>
                      {font.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Line Items */}
      <Card>
        <CardContent className="p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900">Line Items</h3>
            <Button
              type="button"
              onClick={addLineItem}
              className="bg-primary hover:bg-primary-dark"
            >
              <i className="fas fa-plus mr-2"></i>Add Item
            </Button>
          </div>

          {/* Desktop Table View */}
          <div className="hidden md:block overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border-b border-gray-200">
                  <th className="text-left py-3 px-2 font-medium text-gray-700">Description</th>
                  <th className="text-center py-3 px-2 font-medium text-gray-700 w-20">Qty</th>
                  <th className="text-right py-3 px-2 font-medium text-gray-700 w-24">Rate</th>
                  <th className="text-right py-3 px-2 font-medium text-gray-700 w-24">Amount</th>
                  <th className="w-12"></th>
                </tr>
              </thead>
              <tbody>
                {lineItems.map((item, index) => (
                  <tr key={index} className="border-b border-gray-100">
                    <td className="py-3 px-2">
                      <Input
                        value={item.description}
                        onChange={(e) => handleLineItemChange(index, "description", e.target.value)}
                        placeholder="Service description"
                        className="border-gray-300"
                      />
                    </td>
                    <td className="py-3 px-2">
                      <Input
                        type="number"
                        min="1"
                        value={item.quantity}
                        onChange={(e) => handleLineItemChange(index, "quantity", parseInt(e.target.value) || 1)}
                        className="text-center border-gray-300"
                      />
                    </td>
                    <td className="py-3 px-2">
                      <Input
                        type="number"
                        min="0"
                        step="0.01"
                        value={item.rate}
                        onChange={(e) => handleLineItemChange(index, "rate", e.target.value)}
                        className="text-right border-gray-300"
                      />
                    </td>
                    <td className="py-3 px-2 text-right font-medium">
                      ${item.amount}
                    </td>
                    <td className="py-3 px-2 text-center">
                      {lineItems.length > 1 && (
                        <Button
                          type="button"
                          variant="ghost"
                          size="sm"
                          onClick={() => removeLineItem(index)}
                          className="text-red-500 hover:text-red-700 hover:bg-red-50"
                        >
                          <i className="fas fa-trash-alt"></i>
                        </Button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Mobile Vertical Layout */}
          <div className="md:hidden space-y-4">
            {lineItems.map((item, index) => (
              <div key={index} className="border border-gray-200 rounded-lg p-4 bg-gray-50">
                {/* First row: Service name */}
                <div className="mb-3">
                  <Label className="text-sm font-medium text-gray-700 mb-1 block">Service Description</Label>
                  <Input
                    value={item.description}
                    onChange={(e) => handleLineItemChange(index, "description", e.target.value)}
                    placeholder="Service description"
                    className="border-gray-300"
                  />
                </div>

                {/* Second row: Qty and Rate */}
                <div className="grid grid-cols-2 gap-3 mb-3">
                  <div>
                    <Label className="text-sm font-medium text-gray-700 mb-1 block">Quantity</Label>
                    <Input
                      type="number"
                      min="1"
                      value={item.quantity}
                      onChange={(e) => handleLineItemChange(index, "quantity", parseInt(e.target.value) || 1)}
                      className="text-center border-gray-300"
                    />
                  </div>
                  <div>
                    <Label className="text-sm font-medium text-gray-700 mb-1 block">Rate</Label>
                    <Input
                      type="number"
                      min="0"
                      step="0.01"
                      value={item.rate}
                      onChange={(e) => handleLineItemChange(index, "rate", e.target.value)}
                      className="text-right border-gray-300"
                    />
                  </div>
                </div>

                {/* Third row: Amount and Delete button */}
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <span className="text-sm font-medium text-gray-700 mr-3">Amount:</span>
                    <span className="text-lg font-bold text-gray-900">${item.amount}</span>
                  </div>
                  {lineItems.length > 1 && (
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      onClick={() => removeLineItem(index)}
                      className="text-red-500 hover:text-red-700 hover:bg-red-50 ml-2"
                    >
                      <i className="fas fa-trash-alt mr-1"></i>Delete
                    </Button>
                  )}
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

    </div>
  );
}

client/src/components/invoice-preview.ts.content:
import { useMemo, useState, useEffect } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { generateQRCode } from "@/lib/qr-generator";
import { getCurrencySymbol, getTemplateById } from "@/lib/invoice-templates";
import type { InvoiceFormData, LineItemFormData } from "@/types/invoice";

interface InvoicePreviewProps {
  formData: InvoiceFormData;
  lineItems: LineItemFormData[];
  onFormChange: (field: keyof InvoiceFormData, value: any) => void;
}

export default function InvoicePreview({
  formData,
  lineItems,
  onFormChange,
}: InvoicePreviewProps) {
  const qrCodeUrl = useMemo(() => {
    if (formData.isHosted) {
      // For preview, show a sample URL since we don't have the actual invoice ID yet
      // Use current window location to get the correct domain
      const baseUrl = window.location.origin;
      return `${baseUrl}/view/preview`;
    }
    return null;
  }, [formData.isHosted]);

  const [qrCodeDataURL, setQRCodeDataURL] = useState<string | null>(null);

  useEffect(() => {
    if (qrCodeUrl) {
      generateQRCode(qrCodeUrl)
        .then(setQRCodeDataURL)
        .catch(console.error);
    } else {
      setQRCodeDataURL(null);
    }
  }, [qrCodeUrl]);

  const subtotal = lineItems.reduce((sum, item) => {
    const amount = parseFloat(item.rate || "0") * (item.quantity || 0);
    return sum + amount;
  }, 0);

  const taxAmount = (subtotal * parseFloat(formData.taxPercentage || "0")) / 100;
  const shippingAmount = parseFloat(formData.shippingCost || "0");
  const total = subtotal + taxAmount + shippingAmount;

  const template = getTemplateById(formData.template || "classic");
  const currencySymbol = getCurrencySymbol(formData.currency || "USD");

  return (
    <Card className="md:rounded-xl">
      <CardContent className="p-6 md:rounded-xl" style={{ 
        fontFamily: formData.fontFamily || template.fontFamily,
        backgroundColor: template.backgroundColor 
      }}>
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-4 gap-3">
          <h3 className="text-lg font-semibold text-gray-900">Invoice Preview</h3>
          {/* QR Code positioned top-right */}
          <div className="bg-gray-100 p-2 rounded-lg self-start sm:self-auto">
            <div className="w-12 h-12 sm:w-16 sm:h-16 bg-white border-2 border-dashed border-gray-300 rounded flex items-center justify-center">
              {formData.isHosted ? (
                qrCodeDataURL ? (
                  <img src={qrCodeDataURL} alt="QR Code" className="w-full h-full object-contain" />
                ) : (
                  <div className="text-xs text-center text-gray-500">
                    <i className="fas fa-qrcode text-sm sm:text-lg mb-1"></i>
                    <div className="text-xs">QR Code</div>
                  </div>
                )
              ) : (
                <div className="text-xs text-center text-gray-400">
                  <i className="fas fa-qrcode text-sm sm:text-lg mb-1"></i>
                  <div className="text-xs">Enable hosting</div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Mini Invoice Preview */}
        <div className="border border-gray-200 rounded-lg p-4 bg-gray-50 text-sm">
          <div className="flex justify-between items-start mb-4">
            <div>
              {formData.companyLogo && (
                <div className="w-12 h-8 bg-gray-300 rounded mb-2">
                  <img 
                    src={formData.companyLogo} 
                    alt="Company logo" 
                    className="w-full h-full object-contain rounded"
                  />
                </div>
              )}
              <p className="font-semibold text-gray-800">
                {formData.companyName || "Company Name"}
              </p>
              <p className="text-gray-600 text-xs">
                {formData.companyEmail || "email@company.com"}
              </p>
            </div>
            <div className="text-right">
              <p className="font-semibold text-gray-800">INVOICE</p>
              <p className="text-xs text-gray-600">
                {formData.invoiceNumber || "#INV-000"}
              </p>
            </div>
          </div>

          <div className="border-t border-gray-300 pt-3 mb-3">
            <p className="font-medium text-gray-700 mb-1">Bill To:</p>
            <p className="text-gray-600 text-xs">
              {formData.clientName || "Client Name"}
            </p>
          </div>

          <div className="space-y-2 mb-3">
            {lineItems.length > 0 ? (
              lineItems.map((item, index) => (
                <div key={index} className="flex justify-between text-xs">
                  <span>{item.description || "Service"}</span>
                  <span>${item.amount || "0.00"}</span>
                </div>
              ))
            ) : (
              <div className="flex justify-between text-xs text-gray-400">
                <span>No items added</span>
                <span>$0.00</span>
              </div>
            )}
          </div>

          <div className="border-t border-gray-300 pt-2 space-y-1">
            <div className="flex justify-between text-xs">
              <span>Subtotal:</span>
              <span>{currencySymbol}{subtotal.toFixed(2)}</span>
            </div>
            {parseFloat(formData.taxPercentage || "0") > 0 && (
              <div className="flex justify-between text-xs">
                <span>Tax ({formData.taxPercentage}%):</span>
                <span>{currencySymbol}{taxAmount.toFixed(2)}</span>
              </div>
            )}
            {shippingAmount > 0 && (
              <div className="flex justify-between text-xs">
                <span>Shipping:</span>
                <span>{currencySymbol}{shippingAmount.toFixed(2)}</span>
              </div>
            )}
            <div className="flex justify-between font-semibold text-sm border-t border-gray-200 pt-1" style={{ color: formData.primaryColor || template.primaryColor }}>
              <span>Total:</span>
              <span>{currencySymbol}{total.toFixed(2)}</span>
            </div>
          </div>
        </div>

        {/* Hosting & Security Options */}
        <div className="mt-4 space-y-4 p-3 bg-gray-50 rounded-lg border">
          <div className="flex items-start space-x-3">
            <Checkbox
              id="hostInvoice"
              checked={formData.isHosted}
              onCheckedChange={(checked) => onFormChange("isHosted", !!checked)}
              className="mt-0.5 data-[state=checked]:bg-blue-600 data-[state=checked]:border-blue-600"
            />
            <div className="flex-1">
              <Label htmlFor="hostInvoice" className="text-sm font-medium text-gray-800 cursor-pointer leading-relaxed">
                Host online with unique URL
              </Label>
              <p className="text-xs text-gray-500 mt-1">Allow clients to view and pay online</p>
            </div>
          </div>
          <div className="flex items-start space-x-3">
            <Checkbox
              id="passwordProtect"
              checked={formData.isPasswordProtected}
              onCheckedChange={(checked) => onFormChange("isPasswordProtected", !!checked)}
              className="mt-0.5 data-[state=checked]:bg-blue-600 data-[state=checked]:border-blue-600"
            />
            <div className="flex-1">
              <Label htmlFor="passwordProtect" className="text-sm font-medium text-gray-800 cursor-pointer leading-relaxed">
                Password protect invoice
              </Label>
              <p className="text-xs text-gray-500 mt-1">Require password to view invoice</p>
            </div>
          </div>
          {formData.isPasswordProtected && (
            <div className="ml-7 pt-2">
              <Input
                type="password"
                placeholder="Enter password"
                value={formData.password}
                onChange={(e) => onFormChange("password", e.target.value)}
                className="text-sm w-full"
              />
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/logo-upload.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";

interface LogoUploadProps {
  onLogoChange: (logo: string | null) => void;
  currentLogo?: string | null;
}

export default function LogoUpload({ onLogoChange, currentLogo }: LogoUploadProps) {
  const [isUploading, setIsUploading] = useState(false);
  const { toast } = useToast();

  const handleFileSelect = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    // Validate file type
    if (!file.type.startsWith('image/')) {
      toast({
        title: "Invalid file type",
        description: "Please upload an image file (PNG, JPG, SVG)",
        variant: "destructive",
      });
      return;
    }

    // Validate file size (2MB)
    if (file.size > 2 * 1024 * 1024) {
      toast({
        title: "File too large",
        description: "Please upload an image smaller than 2MB",
        variant: "destructive",
      });
      return;
    }

    setIsUploading(true);

    try {
      const formData = new FormData();
      formData.append('logo', file);

      const response = await fetch('/api/upload-logo', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const { logo } = await response.json();
      onLogoChange(logo);

      toast({
        title: "Logo uploaded",
        description: "Your company logo has been uploaded successfully",
      });
    } catch (error) {
      toast({
        title: "Upload failed",
        description: "Failed to upload logo. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsUploading(false);
    }
  };

  const handleRemoveLogo = () => {
    onLogoChange(null);
    toast({
      title: "Logo removed",
      description: "Company logo has been removed",
    });
  };

  return (
    <div className="space-y-4">
      <label className="block text-sm font-medium text-gray-700">Company Logo</label>

      {currentLogo ? (
        <div className="space-y-3">
          <div className="w-32 h-24 border border-gray-300 rounded-lg overflow-hidden bg-gray-50 flex items-center justify-center">
            <img 
              src={currentLogo} 
              alt="Company logo" 
              className="max-w-full max-h-full object-contain"
            />
          </div>
          <div className="flex space-x-2">
            <Button
              type="button"
              variant="outline"
              size="sm"
              onClick={() => document.getElementById('logo-upload')?.click()}
              disabled={isUploading}
            >
              {isUploading ? (
                <>
                  <i className="fas fa-spinner fa-spin mr-2"></i>
                  Uploading...
                </>
              ) : (
                <>
                  <i className="fas fa-edit mr-2"></i>
                  Change
                </>
              )}
            </Button>
            <Button
              type="button"
              variant="outline"
              size="sm"
              onClick={handleRemoveLogo}
              disabled={isUploading}
            >
              <i className="fas fa-trash mr-2"></i>
              Remove
            </Button>
          </div>
        </div>
      ) : (
        <div
          className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-gray-400 transition-colors cursor-pointer"
          onClick={() => document.getElementById('logo-upload')?.click()}
        >
          <i className="fas fa-cloud-upload-alt text-3xl text-gray-400 mb-2"></i>
          <p className="text-gray-600">
            Drop your logo here or{" "}
            <span className="text-primary font-medium">browse files</span>
          </p>
          <p className="text-xs text-gray-500 mt-1">PNG, JPG, SVG up to 2MB</p>
        </div>
      )}

      <input
        id="logo-upload"
